function decrypt(data) {
    try {
        let mark = decodeURI('%7B%7B%7B%7D%7D%7D');
        if (data && data.indexOf(mark) > -1) {
            let part = data.split(mark)[1];
            let decrypted = com.blankj.utilcode.util.ConvertUtils.bytes2String(
                com.blankj.utilcode.util.EncryptUtils.decryptBase64AES(
                    com.blankj.utilcode.util.ConvertUtils.string2Bytes(part),
                    com.blankj.utilcode.util.ConvertUtils.string2Bytes('OW84U8Eerdb99rtsTXWSILDO'),
                    'desede/CBC/PKCS5Padding',
                    com.blankj.utilcode.util.ConvertUtils.string2Bytes('SK8bncVu')
                )
            );
            if (decrypted) {
                return decrypted;
            }
        }
    } catch (e) {
    }
    return data;
}
