function decrypt(data) {
    try {
        let dataBytes = com.blankj.utilcode.util.EncodeUtils.base64Decode(data);
        let keyBytes = com.blankj.utilcode.util.ConvertUtils.string2Bytes('Z6aESpmGB62yNYsh', 'ASCII');
        let ivBytes = com.blankj.utilcode.util.ConvertUtils.string2Bytes('JNWmRJjiDJ7wHtTh', 'UTF-8');
        let decryptBytes = com.blankj.utilcode.util.EncryptUtils.decryptAES(dataBytes, keyBytes, 'AES/CBC/PKCS5Padding', ivBytes);
        let decrypt = com.blankj.utilcode.util.ConvertUtils.bytes2String(decryptBytes, 'UTF-8');
        return decrypt;
    } catch (error) {
    }
    return data;
}
function fixNumber(number) {
    try {
        if (number < 10) {
            return '0' + number;
        }
        return number;
    } catch (error) {
    }
    return '00';
}
function formatTime(time) {
    try {
        let date = new Date(time * 1000);
        return [date.getFullYear(), '-', fixNumber(date.getMonth() + 1), '-', fixNumber(date.getDate()), ' ', fixNumber(date.getHours()), ':', fixNumber(date.getMinutes()), ':', fixNumber(date.getSeconds())].join('');
    } catch (error) {
    }
    return '';
}
